import React, { useState } from 'react';
import { Container } from './style';
import { useHistory } from 'react-router-dom';


const Home: React.FC = () => {

  const history = useHistory();

  const [ data, setData ] = useState('');

  const handleSubmit = (e: any) => {
    e.preventDefault();
    const userInformation = e.target.elements.value;
    const convertData = JSON.stringify(data[userInformation])
    // const userInformation = JSON.stringify(data[e]);
    localStorage.setItem("email", convertData);
  }

  return(
    <Container>
      <section>
        <div className="card-container">
          <form>
            <h1>S'inscrire pour acheter</h1>
            <label>Adresse Courriel:</label>
            <input onChange={ e => setData(e.target.value)} type="email" required />
            <button onClick={handleSubmit} type="submit">S'inscrire</button>
            <button onClick={() => history.push('/products')}>Suivant</button>
          </form>
        </div>
      </section>
    </Container>
  )
}

export default Home;
