import React, { useState, useEffect } from 'react';
import { Container } from './style';
import api from '../../services/api';
import { useHistory } from 'react-router-dom';

interface IUser{
  id: number;
  name: string;
  email: string;
}

const Home: React.FC = () => {
  const history = useHistory();
  const [ data, setData ] = useState<IUser[]>([]);
  useEffect(() => {
    api.get('').then(
      response => {
        setData(response.data)
      }
    )
  }, [])

  const user = (index: number) => {
    const userInformation = JSON.stringify(data[index]);
    localStorage.setItem(`@Information-${[index]}`, userInformation)
  }

  return(
    <Container>
      <section>
        <div className="card-container">
          <form>
            <h1>S'inscrire pour achéter</h1>
            <label>ID:</label>
            <input type="number" required />
            <label>Prénom:</label>
            <input type="text" required />
            <label>Adresse Courriel:</label>
            <input type="email" required />
            <button onClick={ () => history.push('/products') } >S'inscrire</button>
          </form>
        </div>
      </section>
    </Container>
  )
}

export default Home;
