import React, { useState, useEffect } from 'react';
import { Container } from './style';
import api from '../../services/api';
import { useHistory } from 'react-router-dom';


interface IUser{
  email: string;
}

const Home: React.FC = () => {

  const history = useHistory();

  const [ data, setData ] = useState<IUser[]>([]);
  useEffect(() => {
    api.get('').then(
      response => {
        setData(response.data)
      }
    )
  }, [])

  const handleSubmit = (e: any) => {
    e.preventDefault();
    const userInformation = e.target.elements.userInformation.value;
    localStorage.setItem(`@Information-${[e]}`, userInformation);
    window.location.reload();
  }

  return(
    <Container>
      <section>
        <div className="card-container">
          <form onSubmit={handleSubmit}>
            <h1>S'inscrire pour ach√©ter</h1>
            <label>Adresse Courriel:</label>
            <input type="email" required />
            <button type="submit" onClick={ () => history.push('/products') }>S'inscrire</button>
          </form>
        </div>
      </section>
    </Container>
  )
}

export default Home;
