import React, { useState, useEffect } from 'react';
import { Container } from './style';
import api from '../../services/api';

interface IUser{
  name: string;
  email: string;
}

const Home: React.FC = () => {
  const [ data, setData ] = useState<IUser[]>([]);
  useEffect(() => {
    api.get('').then(
      response => {
        setData(response.data)
      }
    )
  }, [])

  const infoUser = (name: string, email: any) => {
    const userInformation = JSON.stringify(data[email]);
    localStorage.setItem(`@Information-${[name, email]}`, userInformation)
  }

  return(
    <Container>
      <section>
        <div className="card-container">
          <form>
            <h1>S'inscrire pour achéter</h1>
            <label>Prénom:</label>
            <input type="text" required />
            <label>Adresse Courriel:</label>
            <input type="email" required />
            <button type="submit">S'inscrire</button>
          </form>
        </div>
      </section>
    </Container>
  )
}

const form: any = document.getElementById('form');

form.addEventListener('submit', (e) => {
    e.preventDefault();
    let email = document.getElementById('email').value;
    let data = {email,};
    let convertData = JSON.stringify(data);

    localStorage.setItem('lead', convertData);

    let content = document.getElementById('content');

    let loading = `<p>Chargement...</p>`

    let ready = `<p>Prêt !</p>`

    content.innerHTML = loading

    setTimeout(() => {
        content.innerHTML = ready
    }, 1000)
})

export default Home;
