import styled from 'styled-components';

export const Container = styled.div`
  width: 80%;
  margin: auto;

  section {
      height: 100vh;
      width: 100%;
      background: #e2e2;
      display: flex;

      .card-container{
          max-width: 400px;
          margin: 0 auto;
          text-align: center;
      }

      .card-container label{
          text-align: left;
          display: block;
      }

      .card-container h1{
          font-size: 20px;
          color: #000;
          margin-bottom: 30px;
      }

      .card-container input, .card-container textarea, .card-container emailarea{
          width: 100%;
          padding: 6px 10px;
          border: 1px solid #ddd;
          box-sizing: border-box;
          display-block;
      }

      .card-container button{
          background: #f1356d;
          color: #fff;
          border: 0;
          margin: 8px;
          padding: 8px;
          border-radius: 8px;
          cursor: pointer;
      }

      .product-content{
          display: grid;
          text-align: center;
          height: 300px;
          background: #fff;
          border-radius: 12px;
          padding: 12px;
      }
  }
`
